const _pkg = require('../lib/index.node')
const chai = require('chai')
// <--

const inputs = [
  {
    a: BigInt(1),
    b: BigInt(1),
    gcd: BigInt(1)
  },
  {
    a: BigInt(0),
    b: BigInt(189),
    gcd: BigInt(189)
  },
  {
    a: BigInt(189),
    b: BigInt(0),
    gcd: BigInt(189)
  },
  {
    a: BigInt(0),
    b: BigInt(0),
    gcd: BigInt(0)
  },
  {
    a: BigInt(1),
    b: BigInt('14546149867129487614601346814'),
    gcd: BigInt(1)
  },
  {
    a: BigInt(27),
    b: BigInt(18),
    gcd: BigInt(9)
  },
  {
    a: BigInt(-27),
    b: BigInt(18),
    gcd: BigInt(9)
  },
  {
    a: BigInt(256),
    b: BigInt(128),
    gcd: BigInt(128)
  },
  {
    a: BigInt(''),
    b: BigInt('') * BigInt(''),
    gcd: BigInt('')
  }
]

describe('gcd', function () {
  for (const input of inputs) {
    describe(`gcd(${input.a}, ${input.b})`, function () {
      it(`should return ${input.gcd}`, function () {
        const ret = _pkg.gcd(input.a, input.b)
        chai.expect(ret).to.equal(input.gcd)
      })
    })
  }
})
